#include "stdio.h"
#include "vector"
#include "queue"
#include "iostream"

using namespace std;

const int inf = 1e9;
#define SZ 1010
#define SZ1 110

struct data
{
    int node, gas, cost;
    bool operator < (const data &x) const
    {
        return cost > x.cost;
    }
    data()
    {

    }
    data(int a,int b,int c)
    {
        node = a;
        gas = b;
        cost = c;
    }
};

int dist[SZ][SZ1], price[SZ], n;
vector <data> adj[SZ];
priority_queue <data> pq;

int dijkstra(int start, int end, int cap)
{
    while(!pq.empty()) pq.pop();

    int i, j, k, v;
    data p;

    for(i=0; i<n; i++)
        for(j=0; j<=cap; j++)
            dist[i][j] = inf;
    dist[start][0]= 0;

    pq.push( data(start, 0, 0) );

    while(!pq.empty())
    {
        p = pq.top();
        pq.pop();

//        printf("end = %d\n",end);
        if(p.node == end) return p.cost;

        if(p.gas+1 <= cap && p.cost + price[p.node] < dist[p.node][p.gas+1])
        {
            dist[p.node][p.gas+1] = p.cost + price[p.node];
            pq.push( data(p.node, p.gas+1, p.cost+price[p.node]) );
        }

        k = adj[p.node].size();
//        printf("from: %d\n",p.node);
        for(i=0; i<k; i++)
        {
            j = adj[p.node][i].node;
            v = adj[p.node][i].cost;
//            printf("to: %d cost: %d\n",j,v);
//            printf("j = %d\n",j);
            if(p.gas < v) continue;
            if(dist[j][p.gas-v] > p.cost)
            {
                dist[j][p.gas-v] = p.cost;
                pq.push( data(j, p.gas-v, p.cost) );
            }
        }
    }

//    return -;
    return -1;
}

int main()
{
    int m, q, i, j, k, l;

    scanf("%d %d",&n,&m);

    for(i=0; i<n; i++)
        scanf("%d",&price[i]);

    for(i=0; i<m; i++)
    {
        scanf("%d %d %d",&j,&k,&l);
        adj[j].push_back( data(k, 0, l) );
        adj[k].push_back( data(j, 0, l) );
    }

    scanf("%d",&q);

    while(q--)
    {
        scanf("%d %d %d",&l,&i,&j);
        k = dijkstra(i, j, l);
        if(k == -1) printf("impossible\n");
        else  printf("%d\n",k);
    }

    return 0;
}
